module.exports = "html {
  display: flex;
  height: 100%;
}
body {
  display: flex;
  flex: 1;
  margin: 0;
}
body > button {
  border-radius: 100px;
  width: 200px;
  height: 200px;
  margin: auto;
}
body > canvas {
  flex: 0;
  margin: auto;
}
";
